import 'dart:io';import 'package:firebase_storage/firebase_storage.dart';import 'package:flutter/material.dart'; class ImageController{  //conver image for firebase  static Future<String> uploadImageToFirebaseStorage(File imageFile) async {   try {    var storageReference = FirebaseStorage.instance.ref().child('images/${DateTime.now().millisecondsSinceEpoch}');    var uploadTask = storageReference.putFile(imageFile);    var downloadUrl = await (await uploadTask).ref.getDownloadURL();    return downloadUrl;   } catch (e) {    print('Error uploading image to Firebase Storage: $e');    return '';   }  } }