import 'package:driver/firebase/controller/authController.dart';import 'package:driver/utilitys/app_const.dart';import 'package:driver/utilitys/colors.dart';import 'package:driver/view/auth/forgot_password_success.dart';import 'package:driver/widgets/app_inputs.dart';import 'package:driver/widgets/text_widgets.dart';import 'package:flutter/material.dart';import '../../widgets/appButton.dart';class ForgotPassword extends StatefulWidget {  const ForgotPassword({Key? key}) : super(key: key);  @override  State<ForgotPassword> createState() => _ForgotPasswordState();}class _ForgotPasswordState extends State<ForgotPassword> {  final _forgetPasswordKey = GlobalKey<FormState>();  final _email = TextEditingController();  bool _isLoading = false;  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: AppColors.bgColor,      body:SingleChildScrollView(        padding: EdgeInsets.only(top: 100, bottom: 20, right: 20, left: 20),        child: Column(          mainAxisAlignment: MainAxisAlignment.start,          crossAxisAlignment: CrossAxisAlignment.start,          children: [            Image.asset("${AppConst.app_logo}" , height: 140, width: 140,),            SizedBox(height: 30,),            TextWithSubText(              title: "Forgot Password",              subTitle: "Enter your registerd  email. We send password reset  mail to reset your password",            ),            SizedBox(height: 20,),            Form(                key: _forgetPasswordKey,                child: Column(                  children: [                    AppInput(                      title: "Email",                      hintText: "jhon@gmail.com",                      controller: _email,                      validator: (v){                        if(v!.isEmpty){                          return "Email must not be empty.";                        }else{                          return null;                        }                      },                    ),                    SizedBox(height: 30,),                    AppButton(                      onClick: ()async{                        setState(() => _isLoading = true);                        await AuthController.resetPassword(email: _email.text, context: context);                        setState(() => _isLoading = false);                      },                      text: "Continue",                      isLoading: _isLoading,                    ),                    SizedBox(height: 20,),                                  ],                ))          ],        ),      ),    );  }}