import 'package:driver/utilitys/app_const.dart';import 'package:driver/utilitys/colors.dart';import 'package:driver/view/auth/login.dart';import 'package:driver/widgets/app_inputs.dart';import 'package:driver/widgets/text_widgets.dart';import 'package:flutter/material.dart';import '../../widgets/appButton.dart';class ForgotPasswordSuccess extends StatefulWidget {  final String email;  const ForgotPasswordSuccess({Key? key, required this.email}) : super(key: key);  @override  State<ForgotPasswordSuccess> createState() => _ForgotPasswordSuccessState();}class _ForgotPasswordSuccessState extends State<ForgotPasswordSuccess> {  bool _isLoading = false;  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: AppColors.bgColor,      body:SingleChildScrollView(        padding: EdgeInsets.only(top: 100, bottom: 20, right: 20, left: 20),        child: Column(          mainAxisAlignment: MainAxisAlignment.start,          crossAxisAlignment: CrossAxisAlignment.start,          children: [            Image.asset("${AppConst.app_logo}" , height: 140, width: 140,),            SizedBox(height: 30,),            TextWithSubText(              title: "Forgot Password",              subTitle: "Enter your registerd  email. We send password reset  mail to reset your password",            ),            SizedBox(height: 40,),            Center(child: AppButton(              onClick: ()=>Navigator.push(context, MaterialPageRoute(builder: (context) => Login())),              text: "Login",              isLoading: _isLoading,            )),          ],        ),      ),    );  }}