import 'package:driver/utilitys/colors.dart';import 'package:driver/view/auth/login.dart';import 'package:flutter/material.dart';class Flash extends StatefulWidget {  const Flash({Key? key}) : super(key: key);  @override  State<Flash> createState() => _FlashState();}class _FlashState extends State<Flash> {  @override  void initState() {    // TODO: implement initState    super.initState();    Future.delayed(Duration(seconds: 2), () {      // 5s over, navigate to a new page     Navigator.push(context, MaterialPageRoute(builder: (context)=>Login()));    });  }  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: AppColors.bgColor,      body: Center(child: Image.asset("assets/images/logo.png" , height: 100, width: 100,),)    );  }}