import 'package:driver/utilitys/colors.dart';import 'package:flutter/material.dart';class HomeSearch extends StatelessWidget {  const HomeSearch({    super.key, required this.controller, required this.onFilter, this.onChanged,  });  final TextEditingController controller;  final VoidCallback onFilter;  final Function(String)? onChanged;  @override  Widget build(BuildContext context) {    return Row(      children: [        Expanded(child: TextFormField(          controller: controller,          onChanged: onChanged,          decoration: InputDecoration(              hintText: "Search your dream car",              hintStyle: TextStyle(                  fontWeight: FontWeight.w400,                  color: Colors.grey,                  fontSize: 13              ),              fillColor: AppColors.white,              filled: true,              contentPadding: EdgeInsets.only(left: 10, right: 10),              border: OutlineInputBorder(                  borderRadius: BorderRadius.circular(100),                  borderSide: BorderSide(width: 1, color: AppColors.grey200)              ),              enabledBorder: OutlineInputBorder(                  borderRadius: BorderRadius.circular(100),                  borderSide: BorderSide(width: 1, color: AppColors.grey200)              ),              focusedBorder: OutlineInputBorder(                  borderRadius: BorderRadius.circular(100),                  borderSide: BorderSide(width: 1, color: AppColors.grey200)              ),              prefixIcon: Icon(Icons.search, color: AppColors.grey200, size: 20,)          ),        )),        SizedBox(width: 10,),        InkWell(          onTap: onFilter,          child: Container(            width: 50,            height: 50,            decoration: BoxDecoration(              color: AppColors.black,              borderRadius: BorderRadius.circular(100),            ),            child: Center(                child: Icon(Icons.filter_list_outlined, color: AppColors.white, size: 30,)            ),          ),        )      ],    );  }}