import 'package:driver/utilitys/colors.dart';import 'package:flutter/material.dart';class Menus extends StatelessWidget {  const Menus({    super.key,    required this.text,    required this.onClick,  });  final String text;  final VoidCallback onClick;  @override  Widget build(BuildContext context) {    return Container(        margin: EdgeInsets.only(bottom: 10),        decoration: BoxDecoration(            borderRadius: BorderRadius.circular(10)        ),        child: ListTile(      onTap: onClick,      title: Text("$text",        style: TextStyle(fontWeight: FontWeight.w500,fontSize: 15,color: Colors.black),),      trailing: Icon(Icons.double_arrow,size: 25,color: Colors.grey.shade600,),      tileColor: AppColors.aapbg    ));  }}