import 'package:flutter/material.dart';import '../utilitys/colors.dart';class AppButton extends StatelessWidget {  const AppButton({    super.key, required this.text,    this.width = 100.00,    this.height = 40.00,    this.bgColor = AppColors.black,    required this.onClick,    this.isLoading = false,  });  final String text;  final Color bgColor;  final double width;  final double height;  final VoidCallback onClick;  final bool isLoading;  @override  Widget build(BuildContext context) {    return InkWell(        onTap:onClick,        child: Container(          padding: EdgeInsets.all(5),          width: width,          height: height,          decoration: BoxDecoration(            color: isLoading ? AppColors.black.withOpacity(0.5) : bgColor,            borderRadius: BorderRadius.circular(100),          ),          child: Center(            child: isLoading ? CircularProgressIndicator(color: AppColors.white,) : Text("$text",              style: TextStyle(                  fontWeight: FontWeight.w500,                  color: AppColors.white,                  fontSize: 17              ),            ),),        )    );  }}