import 'package:flutter/material.dart';class AppAlert{  static Future<void> showMyDialog({required BuildContext context, required String text, required VoidCallback onOk}) async {    return showDialog<void>(      context: context,      barrierDismissible: false, // user must tap button!      builder: (BuildContext context) {        return AlertDialog(          title: Text('Are you sure?'),          content: SingleChildScrollView(            child: ListBody(              children: <Widget>[                Text(text),              ],            ),          ),          actions: <Widget>[            TextButton(              child: Text('YES'),              onPressed:onOk,            ),            TextButton(              child: Text('NO'),              onPressed: () {                Navigator.of(context).pop();              },            ),          ],        );      },    );  }}