import 'package:driver/utilitys/colors.dart';import 'package:flutter/material.dart';class AppInput extends StatelessWidget {  const AppInput({    super.key,    this.title = "",    required this.hintText,    required this.controller,    this.obscureText = false,    this.suffixIcon,    this.prefixIcon,    this.validator,    this.readOnly = false,    this.onChanged,    this.onTap  });  final String title;  final String hintText;  final TextEditingController controller;  final bool obscureText;  final Widget? suffixIcon;  final Widget? prefixIcon;  final String? Function(String?)? validator;  final bool readOnly;  final Function(String?)? onChanged;  final VoidCallback? onTap;  @override  Widget build(BuildContext context) {    return Column(      mainAxisAlignment: MainAxisAlignment.start,      crossAxisAlignment: CrossAxisAlignment.start,      children: [       title != "" ? Padding(          padding: const EdgeInsets.only(left: 8.0),          child: Text("$title",          style: TextStyle(              fontWeight: FontWeight.w500,              color: AppColors.black,              fontSize: 17          ),        ),        ) : SizedBox() ,        SizedBox(height: 5,),        Padding(          padding: const EdgeInsets.all(5.0),          child: TextFormField(            onTap: onTap,          onChanged: onChanged,          controller: controller,          obscureText: obscureText,          readOnly:  readOnly,          decoration: InputDecoration(              fillColor: AppColors.white,              filled: true,              border: OutlineInputBorder(                  borderRadius: BorderRadius.circular(5),                  borderSide: BorderSide.none              ),              enabledBorder: OutlineInputBorder(                  borderRadius: BorderRadius.circular(5),                  borderSide: BorderSide.none              ),              focusedBorder: OutlineInputBorder(                  borderRadius: BorderRadius.circular(5),                  borderSide: BorderSide.none              ),              contentPadding: EdgeInsets.only(left: 15, right: 15),              hintText: "$hintText",              hintStyle: TextStyle(                  fontSize: 13,                  fontWeight: FontWeight.w300              ),            suffixIcon: suffixIcon,            prefixIcon: prefixIcon,          ),          validator: validator,        ),        )      ],    );  }}