import 'package:driver/utilitys/colors.dart';import 'package:flutter/material.dart';class TextWithSubText extends StatelessWidget {  const TextWithSubText({    super.key, required this.title, required this.subTitle,  });  final String title;  final String subTitle;  @override  Widget build(BuildContext context) {    return Column(      mainAxisAlignment: MainAxisAlignment.start,      crossAxisAlignment: CrossAxisAlignment.start,      children: [        Text("$title",          style: TextStyle(              fontSize: 20,              fontWeight: FontWeight.w800,              color: AppColors.black          ),        ),        SizedBox(height: 10,),        Text("$subTitle",          style: TextStyle(              fontSize: 14,              fontWeight: FontWeight.w400,              color: AppColors.black          ),        ),      ],    );  }}